import React, { useState, useEffect } from "react";
import NavBa from "../../components/NavBa/NavBa";
import classes from "./Borwse.module.css";
function Browse() {
  const [meals, setMeals] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [httpError, setHttpError] = useState(false);
  useEffect(() => {
    const fetchMeals = async () => {
      const reponse = await fetch(
        "https://api.themoviedb.org/3//trending/all/week?api_key=d79a59f8d47b95ae03f0e52a9235a4de&language=en-U"
      );

      if (!reponse.ok) {
        throw new Error("something went wrong");
      }

      const reponseData = await reponse.json();
      const loadedMeals = [];

      for (const key in reponseData) {
        loadedMeals.push({
          id: key,
          backdrop_path: reponseData[key].backdrop_path,
        });
      }
      setMeals(loadedMeals);
      setIsLoading(false);
    };

    fetchMeals().catch((error) => {
      setIsLoading(false);
      setHttpError(error.message);
    });
  }, []);
  if (isLoading) {
    return (
      <section className={classes.mealsLoading}>
        <p>Loading...</p>
      </section>
    );
  }
  if (httpError) {
    <section className={classes.mealsError}>
      <p>{httpError}</p>
    </section>;
  }

  const mealslist = meals.map((meal) => (
    <div>
      key={meal.id}
      backdrop_path={meal.backdrop_path}
    </div>
  ));
  return (
    <div className="app">
      <NavBa />
      <ul>{mealslist}</ul>
    </div>
  );
}

export default Browse;
