import { useState, useEffect } from "react";

// import { imageBasePath, requests } from "../../constants/index";
import classes from "./Banner.module.css";
const Banner = () => {
  const [dataBanner, setDataBanner] = useState([]);
  useEffect(() => {
    const callApiBanner = async () => {
      const reponseBannerl = await fetch(
        "https://api.themoviedb.org/3/discover/tv?api_key=d79a59f8d47b95ae03f0e52a9235a4de&with_network=123"
      );

      const resultBanner = await reponseBannerl.json();
      console.log(resultBanner);
      const f =
        resultBanner.results[
          Math.floor(Math.random() * resultBanner.results.length - 1)
        ];
      setDataBanner(resultBanner);
    };
    //   request.data.results[
    //     Math.floor(Math.random() * request.data.results.length - 1)
    //   ];
    // const bgurl = dataBanner ? f : "";
    // console.log(bgurl);
    callApiBanner();
  }, []);

  return (
    <div className={classes.banner}>
      {dataBanner &&
        dataBanner.length > 0 &&
        dataBanner.map((item) => {
          return (
            <img
              key={item.id}
              src={`https://image.tmdb.org/t/p/w500${item.poster_path}`}
              alt="fix"
            />
          );
        })}
    </div>
  );
};
export default Banner;
