        ];
      console.log(resultOriginal);
      setDataBanner(resultOriginal.results);
      // setData(loadedBanner);
    };

    callApiOriginal();
  }, []);

  return (
    <div className={classes.banner}>
      {dataBanner &&
        dataBanner.length > 0 &&
        dataBanner.map((item) => {
          return (
            <div>
              <img
                key={item.id}
                src={`https://image.tmdb.org/t/p/w500${item.backdrop_path}`}
                alt="fix"
              />
              <h1>{item.name}</h1>
            </div>
          );
        })}
      <div>
        <button>Play</button>
        <button>Mylist</button>
      </div>
    </div>
  );
};

