import { useState, useEffect } from "react";

// import { imageBasePath, requests } from "../../constants/index";
import classes from "./Banner.module.css";
const Banner = () => {
  const [dataBanner, setDataBanner] = useState();

  useEffect(() => {
    const callApiOriginal = async () => {
      const reponseOriginal = await fetch(
        "https://api.themoviedb.org/3/discover/tv?api_key=d79a59f8d47b95ae03f0e52a9235a4de&with_network=123"
      );
      const resultOriginal = await reponseOriginal.json();
      console.log(resultOriginal.results);

      const f =
        reponseOriginal.results[
          Math.floor(Math.random() * reponseOriginal.results.length - 1)
        ];
      console.log(resultOriginal);
      setDataBanner(f);
      console.log(dataBanner);
    };

    callApiOriginal();
  }, []);
  if (dataBanner !== null) {
    const dataBannerList = dataBanner.map((data) => (
      <div>
        <img
          key={data.id}
          src={`https://image.tmdb.org/t/p/w500${data.backdrop_path}`}
          alt="fix"
          backdrop_path={data.backdrop_path}
        />
      </div>
    ));
  }
  return (
    <div className={classes.banner}>
      {dataBanner &&
        dataBanner.length > 0 &&
        dataBanner.map((item) => {
          return (
            <div
              style={{
                backgroundColor: `https://image.tmdb.org/t/p/w500${item.backdrop_path}`,
              }}
            >
              <img
                key={item.id}
                src={`https://image.tmdb.org/t/p/w500${item.backdrop_path}`}
                alt="fix"
              />
              <h1>{item.name}</h1>
            </div>
          );
        })}
      <div>
        <h2>{dataBanner.results.backdrop_path}</h2>
        <button>Play</button>
        <button>Mylist</button>
      </div>
    </div>
  );
};

export default Banner;
