import useHttp from "../../components/hooks/use-http";
import NavBa from "../../components/NavBa/NavBa";
import { useState } from "react";

function Browse() {
  const [tasks, setTasks] = useState([]);

  const { isLoading, error, sendRequest: fetchTasks } = useHttp();

  useEffect(() => {
    const transfromTasks = (tasksObj) => {
      const loadedTask = [];

      for (const taskKey in tasksObj) {
        loadedTask.push({ id: taskKey, text: tasksObj[taskKey].text });
      }
      setTasks(loadedTask);
    };
    fetchTasks(
      {
        url: "https://api.themoviedb.org/3//trending/all/week?api_key=d79a59f8d47b95ae03f0e52a9235a4de&language=en-US",
      },
      transfromTasks
    );
  }, [fetchTasks]);

  const taskAddHandler = (task) => {
    setTasks((prevTasks) => prevTasks.concat(task));
  };
  return (
    <React.Fragment>
      <NewTask onAddTask={taskAddHandler}></NewTask>
      <Section>
        <NavBa />
        <div
          items={tasks}
          loading={isLoading}
          error={error}
          onFetch={fetchTasks}
        ></div>
      </Section>
    </React.Fragment>
  );
}

export default Browse;

